mapper.select:
      with insert_pyramid AS (
        insert into pyramids(

            , framework_kind_id
            , framework_id
            , conclusion
            , session_id

        )
        values (

              , #{bean.frameworkKindId}
              , #{bean.frameworkId}
              , #{bean.conclusion}
              , #{sessionId}

        )
      returning id)

      	, insert_reasons_${index1} AS (
				  insert into reasons (

              , word
              , explanation
              , another_explanation
              , display_order
              , session_id
              , pyramid_id

          ) 
          select

              , #{reason.word}
              , #{reason.explanation}
              , #{reason.anotherExplanation}
              , #{reason.displayOrder}
              , #{sessionId}
              , id

          from
            insert_pyramid
          returning id)

            , insert_example_${index1}_${index2} AS (
              insert into examples (

                  , explanation
                  , display_order
                  , session_id 
                  , reason_id

              )
              select

                  , #{example.explanation}
                  , #{example.displayOrder}
                  , #{sessionId}
                  , id

              from
                insert_reasons_${index1}
            )


      select id from insert_pyramid;
